  // Qdrant 워크로드 설정
  const [qdrantConfig, setQdrantConfig] = useState({
    useCase: 'agent-context',
    storageSize: 10,
    replicas: 1,
    nodeSelector: ''
  });

  // ComfyUI 워크로드 설정
  const [comfyuiConfig, setComfyuiConfig] = useState({
    useCase: 'image-generation',
    nodeSelector: '',
    gpuCount: 1,
    cpuLimit: '4',
    memoryLimit: '16Gi',
    storageSize: 50
  });

  // Neo4j (Ontology) 워크로드 설정
  const [neo4jConfig, setNeo4jConfig] = useState({
