driver = GraphDatabase.driver(
    "bolt://neo4j.ai-workloads:7687",
    auth=("neo4j", "password")
)

with driver.session() as session:
    # 노드 생성
    session.run("""
        CREATE (d:Document {name: $name})
        CREATE (e:Entity {name: $entity})
        CREATE (d)-[:MENTIONS]->(e)
    """, name="doc1", entity="AI")

    # 관계 쿼리
    result = session.run("""
        MATCH (d:Document)-[:MENTIONS]->(e:Entity)
        RETURN d.name, e.name
    """)`}</pre>
